import React, { useState } from 'react';
import { AiOutlineArrowUp } from 'react-icons/ai';
import './UnderBar.css';

function UnderBar() {

    const [doChat, setDoChat] = useState("");
    const [doChats, setDoChats] = useState([
        {
            // senderId: "1",
            // messageId: "1",
            type: "text",
            text: "안녕하세요, 만나서 반갑습니다.",
            name: "챗봇 이름",
            // time: new Date(toLocaleTimeString()),
        },
        {
        
        }
    ]);
    const onChange = (event) => setDoChat(event.target.value);

    const onSubmit = (event) => {
        event.preventDefault();
 
        if (doChat === "") {
            return;
        }

        setDoChats((currentArray) => [doChat, ...currentArray]);
        setDoChat("");
    }
    return (
        <>
                {doChats.map((item, index) => (
                    <form>
                   <div className="chatBox" type="text" key={index}>{item}</div>
                    </form>
 
                ))}.

            {/* 언더바 레이아웃 및 이벤트 등록 */}
            <div className="underBar">
                <form onSubmit={onSubmit}>
                    <input
                        className="chat"
                        type="text"
                        value={doChat}
                        placeholder="채팅을 입력하세요"
                        onChange={onChange}
                    />
                </form>
                <AiOutlineArrowUp className="btnEnter" onClick={onSubmit} />
                <hr />
            </div>
        </>
    );
}

export default UnderBar;
