import React, { useState } from 'react';
import { AiOutlineArrowUp } from 'react-icons/ai';
import './UnderBar.css';

function UnderBar() {

    // 초기 챗 리스트는 공란이다.
    const [doChat, setDoChat] = useState("");
    // 초키 챗 리스트는 요소가 없는, 비어있는 배열이다.
    const [doChats, setDoChats] = useState([]);

    /**
     * onChange 이벤트를 정의한다.
     * 이벤트가 일어나면,
     * setDoChat의 value 값을 가져온다.
     * "event.target" 이걸 "event", "target" 이렇게 각각 해석하는 게 아니라,
     * 한 뭉텅이로 봐야한다.
     * 해당요소를 가져오려면 저렇게 접근하는 것이다.
     */

    const onChange = (event) => setDoChat(event.target.value);
    // onSumbit 이벤트가 일어나면,
    const onSubmit = (event) => {

        event.preventDefault();
        // toChat이 공란이라면 그대로 리턴한다. 리스트에 추가하지 않는 것이다.
        if (doChat === "") {
            return;
        }
        // doChats의 currentArray 에 toDo 를 새로운 인덱스로 넣는다.
        setDoChats((currentArray) => [doChat, ...currentArray]);
        // doChat을 공란으로 돌려준다.
        setDoChat("");
    }
    return (
        <>
            {/* 챗 리스트가 상하단 바 사이에 생성이 된다. */}
                {doChats.map((item, index) => (
                    <form>
                   <div className="chatBox" type="text" key={index}>{item}</div>
                    </form>
 
                ))}.


            {/* 언더바 레이아웃 및 이벤트 등록 */}
            <div className="underBar">
                <form onSubmit={onSubmit}>
                    <input
                        className="chat"
                        type="text"
                        value={doChat}
                        placeholder="채팅을 입력하세요"
                        onChange={onChange}
                    />
                </form>
                <AiOutlineArrowUp className="btnEnter" onClick={onSubmit} />
                <hr />
            </div>
        </>
    );
}

export default UnderBar;
