import React, { useState } from 'react';
import { AiOutlineArrowUp } from 'react-icons/ai';
import './UnderBar.css';

//setToDo>> setDoChat 으로 바꾸는 중...
function UnderBar() {
    // 채팅 로그가 투두 리스르 생성 기능과 비슷하다고 느껴서
    // "리액트를 다루는 기술" 책에 있는 투두 리스트에서 코드를 가져왔다.
    /*
    * 리액트 컴포넌트에서 동적인 값을 상태(state) 라고 부른다.
    * 사용자 인터랙션을 통해 컴포넌트 상태값을 동적으로 바뀔 경우에는
    * 상태를 관리하는 것이 필요하다.
    * Hooks 중에 useState() 함수가 있는데,
    * 이를 통해 함수형 컴포넌트에서도 상태를 관리할 수 있게 되었다.
    * 위 설명의 출처 링크: https://xiubindev.tistory.com/97
    */
    // 초기 투두 리스트는 공란이다.
    const [doChat, setDoChat] = useState("");
    // 초키 투두 리스트는 요소가 없는, 비어있는 배열이다.
    const [doChats, setDoChats] = useState([]);

    /**
     * onChange 이벤트를 정의한다.
     * 이벤트가 일어나면,
     * setDoChat의 value 값을 가져온다.
     * "event.target" 이걸 "event", "target" 이렇게 각각 해석하는 게 아니라,
     * 한 뭉텅이로 봐야한다.
     * 해당요소를 가져오려면 저렇게 접근하는 것이다.
     */
    const onChange = (event) => setDoChat(event.target.value);
    /**
     * onSumbit 이벤트가 일어나면,
     * 
     */
    const onSubmit = (event) => {
        /**
         * a, submit 태그는 클릭 시 href를 통해 이동하거나, 창이 새로고침된다.
         * submit 버튼을 클릭해도 새로고침하지 않으려면 preventDefault()를 사용한다.         * 
         * 출처: https://programming119.tistory.com/100
         */
        event.preventDefault();
        // toDo가 공란이라면 그대로 리턴한다. 리스트에 추가하지 않는 것이다.
        if (doChat === "") {
            return;
        }
        /*
        * ... 의 역할은 무엇인가?
        * currentArray 를 가져온다는 것이다.
        * 아래 코드는 기존 currentArray 에 ToDo를 새로운 인덱스로 넣어서
        * 새로운 array를 만드는 코드이다.
        * 출처: https://devdongbaek.tistory.com/41
        */
        // ToDos의 currentArray 에 toDo 를 새로운 인덱스로 넣는다.
        setDoChats((currentArray) => [doChat, ...currentArray]);
        // toDo를 공란으로 돌려준다.
        setDoChat("");
    }
    return (
        <>
            {/* 투두리스트가 상하단 바 사이에 생성이 된다. */}
            <ul>
                {/* 
                매핑을 한다.
                */}
                {doChats.map((item, index) => (
                    <li key={index}>{item}</li>
                ))}
            </ul>
            {/* 언더바 레이아웃 및 이벤트 등록 */}
            <div className="underBar">
                <form onSubmit={onSubmit}>
                    <input
                        className="chat"
                        type="text"
                        value={doChat}
                        placeholder="채팅을 입력하세요"
                        onChange={onChange}
                    />
                </form>
                <AiOutlineArrowUp className="btnEnter" onClick={onSubmit} />
                <hr />
            </div>
        </>
    );
}


export default UnderBar;
