import React, {useState} from 'react';
import {AiOutlineArrowUp} from 'react-icons/ai';
import './UnderBar.css';

const UnderBar = (props) => {
    const [input, setInput] = useState('');

    return(
    <form className="underBar" onSubmit={(event) =>{
        event.preventDefault();

        if (!input) {
            return ;
        }

        props.onSubmit(input);

        setInput('');
    }}
    >
    <input
    autoFocus
    className="chat"
    value={input}
    onChange={({ target: {value} }) => {
        setInput(value);
    }}
    />
        <AiOutlineArrowUp className="btnEnter" type="submit" onClick={(event) =>{
        event.preventDefault();

        if (!input) {
            return ;
        }

        props.onSubmit(input);

        setInput('');
    }}/>
    </form>
    );
};

export default UnderBar;
