import React, { useState } from 'react';
import { AiOutlineArrowUp } from 'react-icons/ai';
import './UnderBar.css';

function UnderBar() {
    // 채팅 로그가 투두 리스르 생성 기능과 비슷하다고 느껴서
    // "리액트를 다루는 기술" 책에 있는 투두 리스트에서 코드를 가져왔다.
    /*
    아래 설명의 출처 링크: https://xiubindev.tistory.com/97
    리액트 컴포넌트에서 동적인 값을 상태(state) 라고 부른다.
    사용자 인터랙션을 통해 컴포넌트 상태값을 동적으로 바뀔 경우에는
    상태를 관리하는 것이 필요하다.
    Hooks 중에 useState() 함수가 있는데,
    이를 통해 함수형 컴포넌트에서도 상태를 관리할 수 있게 되었다.
    */
    // 초기 투두 리스트는 공란이다.
    const [toDo, setToDo] = useState("");
    // 초키 투두 리스트는 요소가 없는, 비어있는 배열이다.
    const [toDos, setToDos] = useState([]);

    const onChange = (event) => setToDo(event.target.value);
    const onSubmit = (event) => {
        event.preventDefault();
        if (toDo === "") {
            return;
        }
        setToDos((currentArray) => [toDo, ...currentArray]);
        setToDo("");
    }
    // console.log(toDos);
    // console.log(toDos.map((item, index) => <li key={index}>{item}</li>));
    return (
        <>
            {/* 투두리스트가 상하단 바 사이에 생성이 된다. */}
            <ul>
                {/*
                매핑을 한다.

                */}
                {toDos.map((item, index) => (
                    <li key={index}>{item}</li>
                ))}
            </ul>
            {/* 언더바 레이아웃 및 이벤트 등록 */}
            <div className="underBar">
                <form onSubmit={onSubmit}>
                    <input
                        className="chat"
                        type="text"
                        value={toDo}
                        placeholder="채팅을 입력하세요"
                        onChange={onChange}
                    />
                </form>
                <AiOutlineArrowUp className="btnEnter" onClick={onSubmit} />
                <hr />
            </div>
        </>
    );
}


export default UnderBar;
